mapper.update:
UPDATE REVIEW_BRD
   SET REVIEW_HIT = REVIEW_HIT + 1
 WHERE REVIEW_NO = #{reviewNo}

mapper.select:
SELECT REVIEW_NO, CAMP_NO, ID, NICKNAME, REVIEW_TITLE, REVIEW_STAR, REVIEW_CONTENT, REVIEW_CREATE_DATE, REVIEW_MODIFY_DATE, REVIEW_IP, REVIEW_HIT, PROFILE_IMAGE
  FROM REVIEW_BRD
 WHERE REVIEW_NO = #{reviewNo}

mapper.update:
UPDATE REVIEW_BRD
   SET PROFILE_IMAGE = #{profileImage}, NICKNAME = #{nickname}
 WHERE ID = #{id}
